const mongoose = require("mongoose");
mongoose.Promise = global.Promise;

const MONGODB_URI = "";

let isConnected;

module.exports = connectToDatabase = () => {
    if(isConnected) {
        return Promise.resolve();
    }
    return mongoose.connect(MONGODB_URI).then((db) => {
        isConnected = db.connections[0].readyState
    })
};

